from datetime import datetime

class Stack:
    def __init__(self):
        self.data = ""

    def pop(self, n=1):
        length = len(self.data)
        if length > 0:
            ans = self.data[-n:]
            self.data = self.data[:-n]
        else:
            ans = None
        return ans

    def look(self):
        length = len(self.data)
        if length > 0:
            ans = self.data[length - 1]
        else:
            ans = None
        return ans

    def put(self, x):
        self.data += x

    def is_empty(self):
        return len(self.data) == 0

    def size(self):
        return len(self.data)

    def content(self):
        return self.data

    def clear(self):
        self.data = ""

    def delete(self, n=1):
        self.data = self.data[:-n]

    def write(self, s: str):
        self.data += s

    def lenOfLastLetterSequence(self):
        k = 1
        if len(self.data) == 1: return 1
        while k < len(self.data):
            if self.data[-k-1] != self.data[-k]:
                return k
            k += 1
            if k == len(self.data):
                return k
            #print("k: ", k)
            #print("data: ", len(self.data))
        return 0



class Solution:
    def removeDuplicates(self, s: str, k: int) -> str:
        """

        :param s:
        :param k:
        :return:
        """
        st1 = Stack()
        st2 = Stack()
        curr_letter = ""
        for letter in s:
            if st1.is_empty() or st1.look() == letter:
                st1.put(letter)
                continue
            else:
                if st2.look() == st1.look():
                    lenSequence = st2.lenOfLastLetterSequence()
                    st1.write(lenSequence * st2.look())
                    st2.delete(lenSequence)
                st2.write((st1.size() % k) * st1.look())
                st1.clear()
                st1.put(letter)
                continue
            if st2.look() == st1.look():
                lenSequence = st2.lenOfLastLetterSequence()
                st1.write(lenSequence * st2.look())
                st2.delete(lenSequence)
        if st2.look() == st1.look():
            lenSequence = st2.lenOfLastLetterSequence()
            st1.write(lenSequence * st2.look())
            st2.delete(lenSequence)
        if not st1.is_empty():
            st2.write((st1.size() % k) * st1.look())
            st1.clear()
        return st2.content()









if __name__ == "__main__":
    sol = Solution()
    start_time = datetime.now()
